-- ----------------------------
-- Table structure for book
-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `book_no` varchar(50) NOT NULL,
  `book_name` varchar(30) NOT NULL,
  `book_price` decimal(8,2) NOT NULL,
  `book_author` varchar(50) NOT NULL,
  `book_pub` varchar(50) DEFAULT NULL,
  `book_count` int(11) DEFAULT NULL,
  `book_date` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
  `book_pic` varchar(100) DEFAULT NULL,
  `book_des` varchar(2000) DEFAULT NULL,
  `mng_username` varchar(30) NOT NULL,
  `bt_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`book_no`),
  KEY `mng_username` (`mng_username`),
  KEY `bt_id` (`bt_id`),
  CONSTRAINT `bt_id` FOREIGN KEY (`bt_id`) REFERENCES `book_type` (`bt_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `mng_username` FOREIGN KEY (`mng_username`) REFERENCES `manager` (`mng_username`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

