-- ----------------------------
-- Table structure for orders
-- ----------------------------
DROP TABLE IF EXISTS `orders`;
CREATE TABLE `orders` (
  `orders_no` varchar(30) NOT NULL,
  `users_username` varchar(30) NOT NULL,
  `orders_date` datetime NOT NULL ON UPDATE CURRENT_TIMESTAMP,
  `orders_addr` varchar(100) NOT NULL,
  `orders_rename` varchar(50) NOT NULL,
  `orders_phone` char(11) NOT NULL,
  `orders_price` decimal(6,2) DEFAULT NULL,
  PRIMARY KEY (`orders_no`),
  KEY `users_name` (`users_username`),
  CONSTRAINT `users_name` FOREIGN KEY (`users_username`) REFERENCES `users` (`users_username`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

