  `oi_bcount` int(11) DEFAULT NULL,
  PRIMARY KEY (`oi_id`),
  KEY `orders_no` (`orders_no`),
  KEY `book_no` (`book_no`),
  CONSTRAINT `book_no` FOREIGN KEY (`book_no`) REFERENCES `book` (`book_no`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `orders_no` FOREIGN KEY (`orders_no`) REFERENCES `orders` (`orders_no`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

